#ifndef NET_LINK_RECV_H
#define NET_LINK_RECV_H



/*
********************************************************************************
* 定义模块数据结构
********************************************************************************
*/

typedef struct {
    INT8U index;
    void  (*entryproc)(INT8U cmd, PACKET_BUF_T *packet);
} FUNCENTRY_LINK_T;

/*******************************************************************
** 函数名:     net_link_recv_init
** 函数描述:   link接收模块初始化
** 参数:       无
** 返回:       无
********************************************************************/
void net_link_recv_init(void);

/*******************************************************************
** 函数名:     net_link_register
** 函数描述:   link接收处理注册
** 参数:       [in] type:   协议类型
**             [in] handler:协议处理函数
** 返回:       注册成功返回true，注册失败返回false
********************************************************************/
BOOLEAN net_link_register(INT8U type, void (*handler)(INT8U type, PACKET_BUF_T *packet));

#endif /* end of LINK_RECV_H */
